global.SynJS = global.SynJS || require('synjs');
var mysql      = require('mysql');
var connection = mysql.createConnection({
    database : 'tracker',
    user     : 'tracker',
    password : 'tracker123'
});


function myFunction1()
{
    connection.connect(function(err) {
        SynJS.resume(_synjsContext);
    });
    SynJS.wait();

    var result;
    connection.query('select * from no', function(err, res) {
        result = res;
        SynJS.resume(_synjsContext);
    });
    SynJS.wait();

    for(var key in result)
    {
        var no = result[key].number;
        console.log(no);
        var result2=null;
        connection.query('select * from alphabets', function(err, res2) {
            result2 = res;
            SynJS.resume(_synjsContext);
        });
        SynJS.wait();

        for(var key in result2)
        {
            var alph = result2[key].alphabets;
            console.log(no+"."+alph);
        }
        console.log('\n');

    }

    connection.release();
};

SynJS.run(myFunction1,null, function (ret) {
    console.log('done');
});